import { useState } from 'react';
import Usehttp from '../../Hooks/use-http';

import Section from '../UI/Section';
import TaskForm from './TaskForm';

const NewTask = (props) => {
 const { isLoading, error, sendRequest: sendTask} = Usehttp(addTask);

const enterdTask = (taskText) => {
  sendTask({
   method: 'POST',
  body: taskText,
  headers: {
    'Content-Type': 'application/json',
  },
  },)
}


 function addTask(taskText){

     const generatedId = taskText.name; // firebase-specific => "name" contains generated id
     const createdTask = { id: generatedId, text: taskText.text };

     props.onAddTask(createdTask);

 }

  return (
    <Section>
      <TaskForm onEnterTask={enterdTask} loading={isLoading} />
      {error && <p>{error}</p>}
    </Section>
  );
};

export default NewTask;
